/**
 *
 * After Attack
 *
 */

// ------------------------------------------------
// C U S T O M I Z E    T H E    A N I M A T I O N
// ------------------------------------------------
//
const BOW_ANIMATION = 'blfx.weapon.range.bow2.physical.shot.attack1.red';
const ARROW_ANIMATION = 'blfx.weapon.range.snipe.arrow2.physical.impact1.red';
const SCALE = 0.15; // Size of the bow animation. Default value: 0.15
const OFFSET = 0.3; // Default value: 0.3
//
// ------------------------------------------------

if (canvas.tokens.controlled.length === 0 || game.user.targets.size === 0) {
  ui.notifications.warn('Please select a source and a target token!');
  return;
}

const myToken = canvas.tokens.controlled[0];
const targetToken = Array.from(game.user.targets)[0];
const sourceElevation = myToken.document.elevation;
const targetElevation = targetToken.document.elevation;
const offset = OFFSET * myToken.document.width;

let isMiss = false;
if (typeof roll !== 'undefined' && roll !== null) {
  isMiss = roll.total < targetToken.actor.system.attributes.ac.value;
}

let impactPoint = targetToken.center;
if (isMiss) {
  impactPoint = blfx.helpers.getTokenCorner(myToken, targetToken);
}

await new Sequence()

  // BOW_ANIMATION
  .effect()
  .file(BOW_ANIMATION)
  .elevation(sourceElevation)
  .atLocation(myToken)
  .rotateTowards(impactPoint)
  .scale(SCALE)
  .center()
  .spriteOffset({ x: offset }, { gridUnits: true })
  .waitUntilFinished(-500)

  // ARROW
  .effect()
  .file(ARROW_ANIMATION)
  .elevation(targetElevation)
  .atLocation(myToken)
  .stretchTo(impactPoint)
  .waitUntilFinished(-300)

  .play();
