// @bakanabaka

console.warn(arguments);
async function preItemRoll() {
    let persistentData = await DAE.getFlag(actor, persistentDataName) || defaultPersistentData;
    if (!persistentData.workflowId) {
        ui.notifications.error(`${macroItem.name} can only be called from within a workflow.`);
        workflow.aborted = true;
        return false;
    }

    let configs = {consumeUsage : false};
    let options = {};
    macroUtil.item.preItemRoll.config(workflow, configs, options);
    // Jank -- use the uses to track the number of times it has occurred
    //    we do this because it automatically will reset on short rest this way
    const updates = {"system.save.dc" : 5 * macroItem.system.uses.value};
    workflow.item = await macroUtil.item.syntheticItem(workflow.item, actor, updates);
}

async function postSave () {
    let persistentData = await DAE.getFlag(actor, persistentDataName) || defaultPersistentData;
    persistentData.isActive = (workflow.saves.size > 0);
    await DAE.setFlag(actor, persistentDataName, persistentData);
}

async function preTargetDamageApplication() {
console.warn("in pretarget");
    if (!(actor.effects.find(ef => ef.name == "Rage"))) return;
console.warn("rage active");
    if (workflow.damageItem.oldHP == 0) return;
console.warn("has health");
    if (workflow.damageItem.oldHP != workflow.damageItem.hpDamage) return;
console.warn("am dying");
    let persistentData = await DAE.getFlag(actor, persistentDataName) || defaultPersistentData;
    persistentData.workflowId = workflow.id;
    await DAE.setFlag(actor, persistentDataName, persistentData);
console.warn("flags set");
    await MidiQOL.completeItemUse(macroItem, {}, {});
console.warn("item used");
    await macroItem.update({"system.uses.value" : macroItem.system.uses.value + 1});

    persistentData = await DAE.getFlag(actor, persistentDataName) || defaultPersistentData;
    persistentData.workflowId = undefined;
    if (persistentData.isActive) workflow.damageItem.hpDamage -= 1;
    await DAE.setFlag(actor, persistentDataName, persistentData);
}

const persistentDataName = `(Relentless Rage) - Persistent Data`;
const defaultPersistentData = { isActive : false, workflowId : undefined };

const callArguments = {
    speaker:    speaker,
    actor:      actor,
    token:      token,
    character:  character,
    item:       item,
    args:       args,
    scope:      scope,
};
await macroUtil.runWorkflows(callArguments, {
    preItemRoll  : preItemRoll,
    preTargetDamageApplication  : preTargetDamageApplication,
    postSave : postSave
});

let lightSource = "torch";
let file = "modules/gambits-premades/assets/images/torch.webp";
let torchStatus = Sequencer.EffectManager.getEffects({ name: `${token.document.id} ${lightSource}` });

async function updateTorch() {
    if (args[0].macroPass === "preApplyDynamicEffects") {
        if (torchStatus.length === 0 && (macroItem.system.uses.value !== 0 || macroItem.system.quantity !== 1)) {
            await showTorchLightDialog();
        } else if (torchStatus.length !== 0) {
            await showTorchOptionsDialog();
        }
        else {
            ui.notifications.warn("You do not have any torches remaining.")
        }
    }

    if (args[0] === "off" && args[1]["expiry-reason"] && (args[1]["expiry-reason"].includes("times-up") || args[1]["expiry-reason"].includes("effect-deleted"))) {
        const light = { dim: 0, bright: 0 };
        await token.document.update({ light });
        await Sequencer.EffectManager.endEffects({ name: `${token.document.id} ${lightSource}`, object: token });
    }
}

async function showTorchLightDialog() {
    return new Promise((resolve) => {
        new Dialog({
            title: "Torch",
            content: "<p>Would you like to light a torch?</p>",
            buttons: {
                turnon: {
                    label: "Light",
                    callback: async (html) => {
                        await lightNewTorch();
                        resolve();
                    }
                }
            },
            default: "turnon"
        }).render(true);
    });
}

async function showTorchOptionsDialog() {
    return new Promise((resolve) => {
        let buttons = {
            turnoff: {
                label: "Extinguish",
                callback: async (html) => {
                    const light = {dim: 0, bright: 0};
                    await token.document.update({light});
                    await Sequencer.EffectManager.endEffects({ name: `${token.document.id} ${lightSource}`, object: token });
                    const effectName = await actor.effects.find(i => i.name === "Torch");
                    if(effectName) await actor.deleteEmbeddedDocuments("ActiveEffect", [effectName.id]);
                    resolve();
                }
            }
        };

        if (macroItem.system.uses.value !== 0 || macroItem.system.quantity !== 1) {
            buttons.turnon = {
                label: "Light New",
                callback: async (html) => {
                    await Sequencer.EffectManager.endEffects({ name: `${token.document.id} ${lightSource}`, object: token });
                    await lightNewTorch();
                    resolve();
                }
            };
        }

        new Dialog({
            title: "Torch",
            content: "<p>What would you like to do with your torch?</p>",
            buttons: buttons,
            default: "turnoff"
        }).render(true);
    });
}

