createTemplate.command:
/**
 *
 * After Template Creation
 * Burning Hands
 *
 */

const myToken = canvas.tokens.controlled[0] ?? sourceToken;
const sourceElevation = myToken.document.elevation; 

await blfx.helpers.wait(100);

await new Sequence()

  .effect()
  .file('jb2a.cone_of_cold.blue')
  .attachTo(sourceToken, { offset: { x: 0 }, gridUnits: true })
  .stretchTo(templateDocument, { attachTo: true })
  .elevation(sourceElevation)
  .waitUntilFinished()
  .play();

await templateDocument.delete();

