macro.command:
if(!game.modules.get("portal-lib")?.active) return ui.notifications.error("You must install the Portal module to use this automation.");
if(!game.modules.get("animated-spell-effects-cartoon")?.active) return ui.notifications.error("You must install the Jack Kerouac's Animated Spell Effects - Cartoon module to use this automation.");
if (args[0].macroPass === "preActiveEffects") {
    if (workflow.targets.size > 1) return ui.notifications.warn("You can only select one creature to teleport along with yourself");
    let target = workflow.targets.first() ?? null;
    if(target) {
        let targetDistance = MidiQOL.computeDistance(target,token,false)
        if(targetDistance > 6) return ui.notifications.warn("You must be within 5 feet of the ally you are trying to teleport");

        const sizeMap = {
            tiny: 1,
            sm: 2,
            med: 3,
            lg: 4,
            huge: 5,
            grg: 6
        };
        let targetValue = sizeMap[target.actor.system.traits.size];
        let initiatingValue = sizeMap[actor.system.traits.size];
        if(targetValue > (initiatingValue + 1)) return ui.notifications.warn("Your target is too large to teleport with you");
    }
    const portalScale = token?.w / canvas.grid.size * 0.7;

    const x1 = token.center.x;
    const y1 = token.center.y;
    const x2 = target?.center.x;
    const y2 = target?.center.y;
    let midpointX;
    let midpointY;
    if(target) {
        midpointX = (x1 + x2) / 2;
        midpointY = (y1 + y2) / 2;
    }
    else {
        midpointX = x1;
        midpointY = y1;
    }

    const magicSign = new Sequence().effect()
        .file('animated-spell-effects-cartoon.magic.portal.red')
        .atLocation({ x: midpointX, y: midpointY })
        .scale(portalScale * 1.5)
        .opacity(1)
        .name(`${token.document.name}_DimensionDoor1`)
        .persist();

    const magicSign2 = new Sequence().effect()
        .file('jb2a.portals.horizontal.ring.dark_red')
        .belowTokens()
        .atLocation(token)
        .scaleToObject(2)
        .opacity(0.5)
        .name(`${token.document.name}_DimensionDoor2`)
        .persist();

    let magicSign3;
    if(target) {
        magicSign3 = new Sequence().effect()
            .file('jb2a.portals.horizontal.ring.dark_red')
            .belowTokens()
            .atLocation(target)
            .scaleToObject(2)
            .opacity(0.5)
            .name(`${token.document.name}_DimensionDoor3`)
            .persist();
    }

    await magicSign.play();
    await magicSign2.play();
    if(target) await magicSign3.play();

    const newLocation = await new Portal()
        .color("#ff0000")
        .texture(token.document.texture.src)
        .origin(token)
        .range(macroItem.system.range.value)
        .pick();

    if (!newLocation) {
        console.error("No valid location picked.");
        Sequencer.EffectManager.endEffects({ name: `${token.document.name}_DimensionDoor1` });
        Sequencer.EffectManager.endEffects({ name: `${token.document.name}_DimensionDoor2` });
        if(target) Sequencer.EffectManager.endEffects({ name: `${token.document.name}_DimensionDoor3` });
        return;
    }

    Sequencer.EffectManager.endEffects({ name: `${token.document.name}_DimensionDoor1` });
    Sequencer.EffectManager.endEffects({ name: `${token.document.name}_DimensionDoor2` });
    if(target) Sequencer.EffectManager.endEffects({ name: `${token.document.name}_DimensionDoor3` });

    const outroSequence = new Sequence();
    outroSequence.effect()
        .file('jb2a.energy_conduit.greenyellow.square')
        .scale(portalScale * 0.5)
        .duration(1200)
        .fadeOut(500)
        .fadeIn(200)
        .atLocation({ x: midpointX, y: midpointY })
        .filter("ColorMatrix", { hue: 280 })
        .stretchTo(newLocation);

    outroSequence.effect()
        .from(token)
        .fadeIn(500)
        .duration(500)
        .zeroSpriteRotation()
        .waitUntilFinished();

    outroSequence.animation()
        .on(token)
        .teleportTo(newLocation, { relativeToCenter: true })
        .opacity(1);

    await outroSequence.play();

    let offsetLocation = newLocation;

    if(target) {
        const tokenSize = target.w;
        const offsetX = tokenSize + 10;

        const offsetLocation = {
            x: newLocation.x + offsetX,
            y: newLocation.y 
        };

        const outroSequence2 = new Sequence();
        outroSequence2.animation()
            .on(target)
            .teleportTo(offsetLocation, { relativeToCenter: true })
            .opacity(1);

        await outroSequence2.play();
    }

    const outroSequence3 = new Sequence().effect()
    .file('animated-spell-effects-cartoon.magic.portal.red')
    .atLocation(offsetLocation)
    .scale(portalScale * 1.5)
    .opacity(1);
    await outroSequence3.play();
}

_data.command:
//DAE Macro Execute, Effect Value = "Macro Name" @target

const lastArg = args[args.length - 1];
let tactor;
if (lastArg.tokenId) tactor = canvas.tokens.get(lastArg.tokenId).actor;
else tactor = game.actors.get(lastArg.actorId);
const target = canvas.tokens.get(lastArg.tokenId)


if (args[0] === "on") {
    ChatMessage.create({ content: `${target.name} turns invisible` });
    target.update({ "hidden": true });
}
if (args[0] === "off") {
    ChatMessage.create({ content: `${target.name} re-appears` });
    target.update({ "hidden": false });
}

data.command:
//DAE Item Macro, no arguments passed
if (!game.modules.get("advanced-macros")?.active) {ui.notifications.error("Please enable the Advanced Macros module") ;return;}

const lastArg = args[args.length - 1];
const target = canvas.tokens.get(lastArg.tokenId)


if (args[0] === "on") {
    ChatMessage.create({ content: `${target.name} turns invisible`, whisper: [game.user] });
    target.update({ "hidden": true });
}
if (args[0] === "off") {
    ChatMessage.create({ content: `${target.name} re-appears`, whisper: [game.user] });
    target.update({ "hidden": false });
}

macro.command:
if (!game.modules.get("warpgate")?.active)
  return ui.notifications.error("Please enable the Warp Gate module");
const initialMutName = "levitationActivation";
if (args[0] !== "off" && args[0] !== "on") {
  const { macroPass, actor, tokenUuid, item, targets } = args[0] ?? {};
  if (!args[0])
    return ui.notifications.error(
      "Something is wrong in the macro of the Item rolled; Notify GM and un-check Item Macros Sheet Hooks option"
    );
  if (!targets.length) return ui.notifications.error("Please select a target");

  const tokenDoc = fromUuidSync(tokenUuid);
  const targetDoc = targets[0];
  const initialTargetUuid = DAE.getFlag(tokenUuid, "levitateMidiSRDTarget");

  if (macroPass === "preItemRoll") {
    console.log(args[0].targets);

    if (!initialTargetUuid || initialTargetUuid === targetDoc.uuid) return true;
    else {
      //if (initialTargetUuid !== targetDoc.uuid)
      await warpgate.revert(tokenDoc, initialMutName);
      await actor.effects
        .find((eff) => eff.label.toLocaleLowerCase().includes("concentrating"))
        ?.delete();
    }
  }
  if (macroPass === "postActiveEffects") {
    if (!initialTargetUuid || initialTargetUuid !== targetDoc.uuid)
      await DAE.setFlag(tokenUuid, "levitateMidiSRDTarget", targetDoc.uuid);
    const content = `<label for="elev"><b><center>Would that be up or down?</center></b></label><br />
                                    <input type="range" step="1" min="-20" max="20" id="elev" name="feet" list="values" />
                                <center><b>Selected value: <output id="value"> ft</output></center></b>
                                <datalist id="values">
                                    <option value="-20" label="-20"></option>
                                    <option value="-10" label="-10"></option>
                                    <option value="0" label="0"></option>
                                    <option value="10" label="10"></option>
                                    <option value="20" label="20"></option>
                                </datalist>`;
    const result = await Dialog.wait({
      title: "Let's Float!",
      content: content,
      default: "no",
      close: () => {
        return false;
      },
      render: () => {
        const value = document.querySelector("#value");
        const input = document.querySelector("#elev");
        value.textContent = input.value;
        input.addEventListener("input", (event) => {
          value.textContent = event.target.value;
        });
      },
      buttons: {
        yes: {
          icon: '<i class="fas fa-check"></i>',
          label: "Float",
          callback: () => {
            const ft = document.querySelector("#elev").value;
            return ft;
          },
        },
        no: {
          icon: '<i class="fas fa-times"></i>',
          label: "Stop concentrating",
          callback: async () => {
            await actor.effects
              .find((eff) =>
                eff.label.toLocaleLowerCase().includes("concentrating")
              )
              ?.delete();
            return false;
          },
        },
      },
      rejectClose: false,
    });
    if (result) {
      const targetUpdates = targetDoc.actor.effects.find(
        (eff) => eff.label === "Levitating"
      )
        ? { token: { elevation: Number(targetDoc.elevation) + Number(result) } }
        : {
            embedded: {
              ActiveEffect: {
                ["Levitating"]: {
                  duration: {
                    startTime: game.time.worldTime,
                    seconds: 600,
                    startRound: game.combat?.round,
                    startTurn: game.combat?.turn,
                  },
                  icon:
                    "icons/magic/control/debuff-energy-hold-levitate-pink.webp",
                  label: "Levitating",
                  origin: item.uuid,
                },
              },
            },
            token: { elevation: Number(targetDoc.elevation) + Number(result) },
          };
      await warpgate.mutate(targetDoc, targetUpdates, {}, { permanent: true });
      await ChatMessage.create({
        content: `${targetDoc.name} floats at ${targetDoc.elevation} ft`,
      });
    }
    if (!result || MidiMacros.hasMutation(tokenDoc, initialMutName)) return;
    const effectUuid = targetDoc.actor.effects.find(
      (eff) => eff.label === "Levitating"
    ).uuid;
    const activation =
      targetDoc.uuid === tokenUuid
        ? { type: "special", cost: "" }
        : item.system.activation;
    let casterUpdates = {
      embedded: {
        Item: {
          [item.name]: {
            name: item.name + " activated",
            system: {
              actionType: "other",
              activation,
              components: { concentration: false },
              level: 0,
              preparation: { mode: "atwill", prepared: true },
              save: { ability: "", dc: "", scaling: "" },
              uses: { value: null, max: "", per: "", recovery: "" },
            },
          },
        },
      },
      actor: {
        flags: {
          ["midi-qol"]: {
            ["concentration-data"]: { removeUuids: [effectUuid] },
          },
          dae: { levitateMidiSRDTarget: targetDoc.uuid },
        },
      },
    };
    await warpgate.mutate(
      tokenDoc,
