macro.command:
let lightSource = "torch";
let file = "modules/gambits-premades/assets/images/torch.webp";
let torchStatus = Sequencer.EffectManager.getEffects({ name: `${token.document.id} ${lightSource}` });

async function updateTorch() {
    if (args[0].macroPass === "preApplyDynamicEffects") {
        if (torchStatus.length === 0 && (macroItem.system.uses.value !== 0 || macroItem.system.quantity !== 1)) {
            await showTorchLightDialog();
        } else if (torchStatus.length !== 0) {
            await showTorchOptionsDialog();
        }
        else {
            ui.notifications.warn("You do not have any torches remaining.")
        }
    }

    if (args[0] === "off" && args[1]["expiry-reason"] && (args[1]["expiry-reason"].includes("times-up") || args[1]["expiry-reason"].includes("effect-deleted"))) {
        const light = { dim: 0, bright: 0 };
        await token.document.update({ light });
        await Sequencer.EffectManager.endEffects({ name: `${token.document.id} ${lightSource}`, object: token });
    }
}

async function showTorchLightDialog() {
    await foundry.applications.api.DialogV2.wait({
    window: { title: 'Torch' },
    content: `
        <div class="gps-dialog-container">
            <div class="gps-dialog-section">
                <div class="gps-dialog-content">
                    <div>
                        <div class="gps-dialog-flex">
                            <p class="gps-dialog-paragraph">Would you like to light a torch?</p>
                            <div id="image-container" class="gps-dialog-image-container">
                                <img src="${macroItem.img}" class="gps-dialog-image">
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    `,
    buttons: [{
        action: "Light",
        label: "Light",
        callback: async (event, button, dialog) => {
            await lightNewTorch();
        }
    }],
    close: async (event, dialog) => {
        return;
    }, rejectClose:false
    });
}

async function showTorchOptionsDialog() {
    await foundry.applications.api.DialogV2.wait({
    window: { title: 'Torch' },
    content: `
        <div class="gps-dialog-container">
            <div class="gps-dialog-section">
                <div class="gps-dialog-content">
                    <div>
                        <div class="gps-dialog-flex">
                            <p class="gps-dialog-paragraph">What would you like to do with your torch?</p>
                            <div id="image-container" class="gps-dialog-image-container">
                                <img src="${macroItem.img}" class="gps-dialog-image">
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    `,
    buttons: [{
        action: "Light New",
        label: "Light New",
        callback: async (event, button, dialog) => {
            await Sequencer.EffectManager.endEffects({ name: `${token.document.id} ${lightSource}`, object: token });
            await lightNewTorch();
        }
    },
    {
        action: "Extinguish",
        label: "Extinguish",
        callback: async (event, button, dialog) => {
            const light = {dim: 0, bright: 0};
            await token.document.update({light});
            await Sequencer.EffectManager.endEffects({ name: `${token.document.id} ${lightSource}`, object: token });
            const effectName = await actor.effects.find(i => i.name === "Torch");
            if(effectName) await actor.deleteEmbeddedDocuments("ActiveEffect", [effectName.id]);
        }
    }],
    close: async (event, dialog) => {
        return;
    }, rejectClose:false
    });
}

async function lightNewTorch() {
 new Sequence()

        .effect()
