                .item-list div {
                    flex-grow: 1;
                    display: flex;
                    align-items: center;
                }
                .item-list img {
                    border: 0;
                    margin-right: 10px;
                    vertical-align: middle; /* Ensure the image is aligned vertically */
                }
                .item-list span {
                    margin-left: 5px;
                }
            </style>
            <ul style='list-style-type: none; padding: 0;'>
        `;
        items.forEach((item) => {
            content += `
                <li id="item-${item.id}" class="item-list">
                    <div>
                        <img src="${item.img}" width="30" height="30">
                        <span>${item.name} (${item.system.quantity})</span>
                    </div>
                    <span id="spellLevelSelect_${item.id}">${spellLevelDropdown}</span>
                    <button type="button" class="identify-btn" data-item-id="${item.id}" style="width: 25%; margin-left: 10px;">Identify ${timeToCast}</button>
                </li>
            `;
        });
        content += "</ul>";

        foundry.applications.api.DialogV2.wait({
            window: { title: (ritual) ? "Identify Items Ritually" : "Identify Items" },
            content: content,
            buttons: [{
                action: "Close",
                label: "<i class='fas fa-times' style='margin-right: 5px;'></i>Close",
            }],
            render: (event) => {
                let dialogElement = event.target.element;
                dialogElement.querySelectorAll('.identify-btn').forEach(button => {
                    button.addEventListener('click', async event => {
                        const itemId = event.currentTarget.dataset.itemId;
                        const selectElement = dialogElement.querySelector(`#spellLevelSelect_${itemId} select`);
                        let castLevel = !ritual ? selectElement?.value : null;

                        if (castLevel) {
                            let currentSlots = actor.system.spells[`spell${castLevel}`].value;
                            await actor.update({ [`system.spells.spell${castLevel}.value`]: currentSlots - 1 });
                            refreshSpellLevelDropdown(dialogElement, actor);
                        }

                        await identifyItem(itemId, dialogElement);
                    });
                });
            },
            close: async (event, dialog) => {
                return;
            }, rejectClose:false
        });
    }

    async function identifyItem(itemId, dialogElement) {
        const itemData = actor.items.get(itemId);
        if (itemData) {
            if (MidiQOL.safeGetGameSetting('gambits-premades', 'Enable Identify Restrictions') === true) {
                await game.gps.socket.executeAsGM('gmIdentifyItem', { itemUuid: itemData.uuid });
            } else {
                await itemData.update({ "system.identified": true });
            }
            ui.notifications.info(`Identified: ${itemData.name}`);

            const itemText = `
                <div style="display: flex; align-items: center;">
                    <img src="${itemData.img}" width="30" height="30">
                    <span><a class="open-item-sheet" data-item-id="${itemData.id}">${itemData.name}</a> (${itemData.system.quantity})</span>
                </div>
            `;

            const listItem = dialogElement.querySelector(`#item-${itemId}`);
            listItem.innerHTML = itemText;
            listItem.querySelector('.open-item-sheet').addEventListener('click', (ev) => {
                itemData.sheet.render(true);
            });
        }
    }

    async function refreshSpellLevelDropdown(dialogElement, actor) {
        const items = actor.items.contents.filter(i => i.system.identified === false);
        const optionBackground = (document.body.classList.contains("theme-dark")) ? 'black' : 'var(--color-bg)';

        let dropdownOptions = '';
        for (let level = 1; level <= 9; level++) {
            let spellSlot = actor.system.spells[`spell${level}`]?.value;
            if (spellSlot > 0) {
                let levelWithOrdinal = getOrdinalNumber(level);
                dropdownOptions += `<option class="gps-dialog-option" style="background-color: ${optionBackground};" value="${level}">${levelWithOrdinal} Level (${spellSlot})</option>`;
            }
        }

        if (dropdownOptions) {
            items.forEach(i => {
                const selectContainer = dialogElement.querySelector(`#spellLevelSelect_${i.id}`);
                if (selectContainer) {
                    selectContainer.innerHTML = `<select style="background-color: rgba(181, 99, 69, 0.2);" class="gps-dialog-select" id="spellLevelDropdown">${dropdownOptions}</select>`;
                }
            });
        }
    }

    function getOrdinalNumber(number) {
        switch (number) {
            case 1: return "1st";
            case 2: return "2nd";
            case 3: return "3rd";
            default: return number + "th";
        }
    }
}

if (args[0].macroPass === "preItemRoll") {
    workflow.config.consumeSpellSlot = null;
    workflow.options.configureDialog = false;
}

data.command:
const casterToken = canvas.tokens.get(args[0].tokenId);
if (!casterToken) {
	ui.notifications.warn("Please select a valid token to use this ability.");
	return;
}

await warpgate.spawn("Mage Hand", {}, {
    pre: async (location) => {
        const seq = new Sequence()
        .effect()
        .file("jb2a.extras.tmfx.runes.circle.outpulse.conjuration")
        .atLocation(casterToken)
        .duration(2000)
        .fadeIn(500)
        .fadeOut(500)
        .scale(0.5)
        .opacity(0.3)
        .filter("Glow", { color: 0xa1c4fd })
        .scaleIn(0, 500, {ease: "easeOutCubic", delay: 100})
    .effect()
        .file("jb2a.moonbeam.01.intro.blue")
        .atLocation(casterToken)
        .fadeIn(100)
        .fadeOut(200)
        .duration(1200)
        .waitUntilFinished(-500)
    .effect()
        .file("jb2a.toll_the_dead.blue.shockwave")
        .atLocation(location)
        .fadeIn(500)
        .fadeOut(500)
        .scale(0.5)
        .scaleIn(0, 500, {ease: "easeOutCubic", delay: 100})
        .filter("Glow", { color: 0xa1c4fd })

    seq.play();

        // Sleep for 500ms
        await (new Promise(resolve => setTimeout(resolve, 1200)));
    }
}, {
    collision: false
});

macro.command:
// Turn a spell scroll into a spell.
// required modules: none.

/**
 * Wait for the specified number of milliseconds.
 *
 * @param {number} ms number of ms to wait.
 */
async function wait(ms) {
  return new Promise((resolve) => setTimeout(resolve, ms));
}

const key = "shareddata.ddb-spells"; // key for the pack where you find the spell.@

const wizard = actor;
const tokenD = token;
if (!wizard) {
  ui.notifications.error("Please select a token.");
  return;
}
const scrolls = wizard.itemTypes.consumable.filter((item) => {
  if (item.system.type.value !== "scroll") return false;
  return item.name.startsWith("Spell Scroll:");
});
if (!scrolls.length) {
  ui.notifications.info("Actor has no scrolls.");
  return;
}
const options = scrolls.reduce((acc, { id, name }) => {
  return (acc += `<option value="${id}">${name}</option>`);
}, "");
const content = `
<form>
  <div class="form-group">
    <label for="item-select">Select the scroll:</label>
    <div class="form-fields">
      <select id="item-select">${options}</select>
    </div>
  </div>
</form>`;
return Dialog.prompt({
  title: "Scroll to Spell",
  content,
  rejectClose: false,
  label: "Create Spell",
  callback: async (html) => {
      const scrollId = html[0].querySelector("#item-select").value;
      console.log("scrollId:", scrollId);

    const scroll = wizard.items.get(scrollId);
    console.log("scroll:", scroll);
      const pack = game.packs.get(key);
      console.log("pack:", pack);
    const spellId = pack.index.getName(
      scroll.name.replace("Spell Scroll: ", "")
    )._id;
    console.log("spellId:", spellId);
    const spell = await pack.getDocument(spellId);
    console.log("spell:", spell);
    const spellLevel = spell.system.level;
    const goldRequired = spellLevel * 50;
    console.log("spellLevel:", spellLevel);
    console.log("goldRequired:", goldRequired);

    // Ensure wizard.system.currency.gp exists before accessing it
    if (wizard.system.currency.gp < goldRequired) {
      ui.notifications.error(
        `${wizard.name} needs ${goldRequired} gp to create this spell.`
      );
      return;
    }
    // Confirmation dialog
    let confirmed = await Dialog.confirm({
      title: "Confirm Purchase",
      content: `<p>Are you sure you want to spend ${goldRequired} gp to create this spell?</p>`,
    });

    if (confirmed) {
      // Intelligence (Arcana) check
      await game.MonksTokenBar.requestRoll([tokenD], {
        request: [{ type: "skill", key: "arc" }],
        dc: 10 + spellLevel,
        silent: true,
      });
      await wait(5000);

        let tokenid = "token" + tokenD.id;
        console.log("tokenid:", tokenid);
        console.log('game.messages.contents[game.messages.contents.length - 1].flags["monks-tokenbar"][tokenid].passed:', game.messages.contents[game.messages.contents.length - 1].flags[
          "monks-tokenbar"
        ][tokenid].passed); 
      if (
        game.messages.contents[game.messages.contents.length - 1].flags[
          "monks-tokenbar"
        ][tokenid].passed
      ) {
        ui.notifications.info(
          `${wizard.name} spends ${goldRequired} gp to create this spell.`
        );

        // Create the spell in the wizard's spellbook
        const createdSpell = await wizard.createEmbeddedDocuments("Item", [
          spell.toObject(),
        ]);
        console.log("createdSpell:", createdSpell);
        if (createdSpell.length > 0) await scroll.delete();

        // Correct syntax for updating wizard.system.currency.gp
        await wizard.update({
          ["system.currency.gp"]: wizard.system.currency.gp - goldRequired,
        });
        return;
      } else {
        ui.notifications.error(
          `${wizard.name} failed and destroyed the scroll.`
        );
        await scroll.delete();
        await wizard.update({
          ["system.currency.gp"]: wizard.system.currency.gp - goldRequired,
        });
      }
    }
  },
});

