// Turn a spell scroll into a spell.
// required modules: none.

/**
 * Wait for the specified number of milliseconds.
 *
 * @param {number} ms number of ms to wait.
 */
async function wait(ms) {
  return new Promise((resolve) => setTimeout(resolve, ms));
}

const key = "shareddata.ddb-spells"; // key for the pack where you find the spell.@

const wizard = actor;
const tokenD = token;
if (!wizard) {
  ui.notifications.error("Please select a token.");
  return;
}
const scrolls = wizard.itemTypes.consumable.filter((item) => {
  if (item.system.type.value !== "scroll") return false;
  return item.name.startsWith("Spell Scroll:");
});
if (!scrolls.length) {
  ui.notifications.info("Actor has no scrolls.");
  return;
}
const options = scrolls.reduce((acc, { id, name }) => {
  return (acc += `<option value="${id}">${name}</option>`);
}, "");
const content = `
<form>
  <div class="form-group">
    <label for="item-select">Select the scroll:</label>
    <div class="form-fields">
      <select id="item-select">${options}</select>
    </div>
  </div>
</form>`;
return Dialog.prompt({
  title: "Scroll to Spell",
  content,
  rejectClose: false,
  label: "Create Spell",
  callback: async (html) => {
      const scrollId = html[0].querySelector("#item-select").value;
      console.log("scrollId:", scrollId);

    const scroll = wizard.items.get(scrollId);
    console.log("scroll:", scroll);
      const pack = game.packs.get(key);
      console.log("pack:", pack);
    const spellId = pack.index.getName(
      scroll.name.replace("Spell Scroll: ", "")
    )._id;
    console.log("spellId:", spellId);
    const spell = await pack.getDocument(spellId);
    console.log("spell:", spell);
    const spellLevel = spell.system.level;
    const goldRequired = spellLevel * 50;
    console.log("spellLevel:", spellLevel);
    console.log("goldRequired:", goldRequired);

    // Ensure wizard.system.currency.gp exists before accessing it
    if (wizard.system.currency.gp < goldRequired) {
      ui.notifications.error(
        `${wizard.name} needs ${goldRequired} gp to create this spell.`
      );
      return;
    }
    // Confirmation dialog
    let confirmed = await Dialog.confirm({
      title: "Confirm Purchase",
      content: `<p>Are you sure you want to spend ${goldRequired} gp to create this spell?</p>`,
    });

    if (confirmed) {
      // Intelligence (Arcana) check
      await game.MonksTokenBar.requestRoll([tokenD], {
        request: [{ type: "skill", key: "arc" }],
        dc: 10 + spellLevel,
        silent: true,
      });
      await wait(5000);

        let tokenid = "token" + tokenD.id;
        console.log("tokenid:", tokenid);
        console.log('game.messages.contents[game.messages.contents.length - 1].flags["monks-tokenbar"][tokenid].passed:', game.messages.contents[game.messages.contents.length - 1].flags[
          "monks-tokenbar"
        ][tokenid].passed); 
      if (
        game.messages.contents[game.messages.contents.length - 1].flags[
          "monks-tokenbar"
        ][tokenid].passed
      ) {
        ui.notifications.info(
          `${wizard.name} spends ${goldRequired} gp to create this spell.`
        );

        // Create the spell in the wizard's spellbook
        const createdSpell = await wizard.createEmbeddedDocuments("Item", [
          spell.toObject(),
        ]);
        console.log("createdSpell:", createdSpell);
        if (createdSpell.length > 0) await scroll.delete();

        // Correct syntax for updating wizard.system.currency.gp
        await wizard.update({
          ["system.currency.gp"]: wizard.system.currency.gp - goldRequired,
        });
        return;
      } else {
        ui.notifications.error(
          `${wizard.name} failed and destroyed the scroll.`
        );
        await scroll.delete();
        await wizard.update({
          ["system.currency.gp"]: wizard.system.currency.gp - goldRequired,
        });
      }
    }
  },
});

